import React from 'react';
import {
  Container,
  Row,
  Col,
  Button,
  Modal,
  ModalHeader,
  ModalBody,
  ModalFooter
} from 'reactstrap';

export default class Example extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      modal: false
    };

    this.toggle = this.toggle.bind(this);
  }

  toggle() {
    this.setState(prevState => ({
      modal: !prevState.modal
    }));
  }

  render() {
    return (
      <Container>
        {' '}
        <Row>
          <Col xs="6" sm="4">
            <Button className="btn-block" color="secondary" size="lg">
              Run
            </Button>
          </Col>
          <Col xs="6" sm="4" />
          <Col sm="4">
            <Button
              className="btn-block"
              color="secondary"
              size="lg"
              onClick={this.toggle}
            >
              Explain
            </Button>
          </Col>
        </Row>
        <Modal
          isOpen={this.state.modal}
          toggle={this.toggle}
          className={this.props.className}
        >
          <ModalHeader toggle={this.toggle}>
            Most reasonable people would never knowingly harm an animal.{' '}
          </ModalHeader>
          <ModalBody>
            Yet we pay for individual animals to be torn apart for their raw
            materials by the million. This madness has to stop. Step behind the
            dreadful shutter and virtually process a single individual who is
            just likeUs.
          </ModalBody>
          <ModalFooter
            style={{
              display: 'flex',
              justifyContent: 'center',
              alignItems: 'center'
            }}
          >
            <Button color="secondary" onClick={this.toggle}>
              Okay
            </Button>
          </ModalFooter>
        </Modal>
      </Container>
    );
  }
}
