import React, { Component } from 'react';

import { Collapse } from 'react-collapse';

import {
  Jumbotron,
  Button,
  Dropdown,
  DropdownToggle,
  DropdownMenu,
  DropdownItem
} from 'reactstrap';

import { Container, Row, Col } from 'reactstrap';
export default class KillPig extends Component {
  constructor(props) {
    super(props);

    this.toggleTools = this.toggleTools.bind(this);
  }
  state = {
    dropdownOpen: false
  };

  toggleTools() {
    this.setState(prevState => ({
      dropdownOpen: !prevState.dropdownOpen
    }));
  }

  render() {
    return (
      <div
        style={{
          position: 'absolute',
          width: '100%',
          height: '100%',
          backgroundColor: 'black',
          left: '0',
          display: 'flex',
          //   justifyContent: 'center',
          //   alignItems: 'center',
          border: '2px solid red'
          //
        }}
      >
        <Container
          id="main-div"
          style={{
            border: '2px solid orange',
            // textAlign: 'center',
            alignContent: 'center',
            justifyContent: 'center',
            alignItems: 'center'
          }}
          align="center"
        >
          <div id="pig-to-kill" />
          <Jumbotron
            style={{ margin: '50px 0px 50px 0', height: '300px', margin: 0 }}
          >
            <h1 className="display-3">Kill the animal.</h1>
            <p className="lead">
              Select a tool from the menu, then click on the pig to kill it.
            </p>
            <Dropdown isOpen={this.state.dropdownOpen} toggle={this.toggle}>
              <DropdownToggle caret>Tools</DropdownToggle>
              <DropdownMenu>
                <DropdownItem header>Tool Menu</DropdownItem>
                <DropdownItem>Some Action</DropdownItem>

                <DropdownItem divider />
                <DropdownItem>Foo Action</DropdownItem>
                <DropdownItem>Bar Action</DropdownItem>
                <DropdownItem>Quo Action</DropdownItem>
              </DropdownMenu>
            </Dropdown>
          </Jumbotron>
        </Container>
      </div>
    );
  }
}
