import React from 'react';
import {
  Container,
  Row,
  Col,
  Button,
  Modal,
  ModalHeader,
  ModalBody,
  ModalFooter
} from 'reactstrap';

export default class Example extends React.Component {
  constructor(props) {
    super(props);

    this.togglemodal = this.togglemodal.bind(this);
    this.state = {
      isOpen: false,
      modal: false
    };
  }

  togglemodal() {
    this.setState(prevState => ({
      modal: !prevState.modal
    }));
  }
  render() {
    return (
      <Container>
        <Modal
          isOpen={this.state.modal}
          togglemodal={this.togglemodal}
          className={this.props.className}
        >
          <ModalHeader togglemodal={this.togglemodal}>likeUs:</ModalHeader>
          <ModalBody id="modbod">
            <i className="fas fa-mobile-alt fa-4x" />
            <br />
            <h3>+49 163 390 5881</h3>
            <br />
            <i className="far fa-envelope fa-4x" />
            <br />
            <h3>roibeard.mac.unfraidh@gmail.com</h3>
          </ModalBody>
          <ModalFooter
            style={{
              display: 'flex',
              alignItems: 'center',
              justifyContent: 'center',
              padding: '20px'
            }}
          >
            <Button color="secondary" onClick={this.togglemodal}>
              done
            </Button>
          </ModalFooter>
        </Modal>{' '}
        <Row>
          <Col xs="6" sm="4">
            <Button color="secondary" size="lg">
              Start
            </Button>
          </Col>
          <Col xs="6" sm="4" />
          <Col sm="4">
            <Button color="secondary" size="lg" onClick={this.togglemodal}>
              Explain
            </Button>
          </Col>
        </Row>
      </Container>
    );
  }
}
