import React, { Component } from 'react';

import { Collapse } from 'react-collapse';

import { Jumbotron, Button } from 'reactstrap';

import { Container, Row, Col } from 'reactstrap';
export default class KillPig extends Component {
  state = {};
  toggleCollapse = () => {
    this.setState({ pSelectIsOpened: true, showMenu: false });
  };

  render() {
    return (
      <div
        style={{
          position: 'absolute',
          width: '100%',
          height: '100%',
          backgroundColor: 'black',
          left: '0',
          display: 'flex',
          //   justifyContent: 'center',
          //   alignItems: 'center',
          border: '2px solid red'
          //
        }}
      >
        <Container
          id="main-div"
          style={{
            border: '2px solid orange',
            // textAlign: 'center',
            alignContent: 'center',
            justifyContent: 'center',
            alignItems: 'center'
          }}
          align="center"
        >
          <div id="pig-to-kill" />
          <Jumbotron style={{ margin: '50px 0px 50px 0', height: '200px' }}>
            <h1 className="display-3">Kill the animal.</h1>
            <p className="lead">
              Select a tool from the menu and then click on the pig to kill it.
            </p>
            <Button color="primary">Learn More</Button>
          </Jumbotron>
        </Container>
      </div>
    );
  }
}
