import React, { Component } from 'react';
import { Jumbotron, Button } from 'reactstrap';
export default class SelectPig extends Component {
  render() {
    return (
      <div>
        <Jumbotron>
          <h1 className="display-3">Select Pig</h1>
          <p className="lead">
            We're going to process a single medium sized pig from snout to tail.
          </p>
          <hr className="my-2" />
          <p>
            Touch an animal to examine it. When you've made your selection, drag
            it to the slaughter area.
          </p>
          <p
            className="lead"
            style={{
              display: 'flex',
              justifyContent: 'center',
              alignItems: 'center'
            }}
          >
            <Button color="primary">Okay</Button>
          </p>
        </Jumbotron>

        <Pig />
      </div>
    );
  }
}
