import React, { Component } from 'react';
import { Jumbotron, Button } from 'reactstrap';
import Pig from './Pig';
export default class SelectPig extends Component {
  selectedPig = '';
  examinePig = () => {
    this.selectedPig = this.id;
    console.log('test');
  };
  render() {
    return (
      <div>
        <Jumbotron>
          <h1 className="display-3">Select Pig</h1>
          <p className="lead">
            We're going to process a single medium sized pig from snout to tail.
          </p>
          <hr className="my-2" />
          <p>
            Touch an animal to examine it. When you've made your selection, drag
            it to the slaughter area.
          </p>
        </Jumbotron>
        <div className="pigholder">
          <Pig id="pig2" onClick={this.examinePig} />
          <Pig id="pig1" onClick={this.examinePig} />
          <Pig id="pig3" onClick={this.examinePig} />
          <Pig id="pig0" onClick={this.examinePig} />
        </div>
      </div>
    );
  }
}
